import { Link } from 'react-router-dom';
import React from 'react';

import { useStateValue } from '../context/StateContext';

function Department() {
    const { staffs, fetchStaff } = useStateValue();
    return (
        <div className="container">
            <ul className='row g-4'>
                {staffs.map(department => (
                    <li key={department.id}
                        className='col-lg-4 col-md-6 col-sm-12'
                        onClick={e => fetchStaff('staffs')}
                    >
                        <Link to={`/Departments/${department.name}`}>
                            <div className='border rounded p-2'>
                                <h3>{department.name}</h3>
                                <p className='p-2'>Số lượng nhân viên: {department.numberOfStaff}</p>
                            </div>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    )
}
export default Department
